package Conditional.decisionmaking;

public class SwitchVowelConsonant {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		char caseCharacter = 'j';
		switch(caseCharacter) {
		case 'a': {
			System.out.println(caseCharacter +" is Vowel");
			break;
		}
		case 'e': {
			System.out.println(caseCharacter +" is Vowel");
			break;
		}
		case 'i': {
			System.out.println(caseCharacter +" is Vowel");
			break;
		}
		case 'o': {
			System.out.println(caseCharacter +" is Vowel");
			break;
		}
		case 'u': {
			System.out.println(caseCharacter +" is Vowel");
			break;
		}
		default: {
			System.out.println(caseCharacter +" is a Consonant");
		}
		}

	}

}
