package Conditional.loops;

public class SumOfNatural {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int n =10;
		int result = 0;
		for( int i = 1; i<=10 ; i++) {
			result +=i;
			
		}
		System.out.println("The Sum of first 10 Natural Number is : "+ result);
	}

}
