package Variables.staticvariables;

public class StaticVariableMain {
	
	static byte b ;
	static short sh;
	 static int n;
	static float fl;
	static double db;
	static long lo;
	static boolean bl;
	static char ch;

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		System.out.println("The Default Value Of Byte: " + b);
		System.out.println("The Default Value Of Short: "+ sh);
		System.out.println("The Default Value Of Integer: "+ n);
		System.out.println("The Default Value Of float: "+ fl);
		System.out.println("The Default Value Of Double: "+ db);
		System.out.println("The Default Value Of Long: "+ lo);
		System.out.println("The Default Value Of Boolean: "+ bl);
		System.out.println("The Default Value Of Character: "+ ch);
	}

}
